import React, { useState, useEffect, useRef, useCallback } from 'react';
import { TestMode, Language, TypingStats } from '../../types';
import { typingAPI } from '../../services/api';

interface TypingTestProps {
  mode: TestMode;
  language: Language;
  onTestComplete: (stats: TypingStats) => void;
}

interface TypingCharacter {
  char: string;
  status: 'correct' | 'incorrect' | 'current' | 'untyped';
  index: number;
}

const TypingTest: React.FC<TypingTestProps> = ({ mode, language, onTestComplete }) => {
  const [displayText, setDisplayText] = useState<string>('');
  const [currentIndex, setCurrentIndex] = useState<number>(0);
  const [userInput, setUserInput] = useState<string>('');
  const [isTestActive, setIsTestActive] = useState<boolean>(false);
  const [isTestComplete, setIsTestComplete] = useState<boolean>(false);
  const [timeLeft, setTimeLeft] = useState<number>(0);
  const [startTime, setStartTime] = useState<Date | null>(null);
  const [wpm, setWpm] = useState<number>(0);
  const [accuracy, setAccuracy] = useState<number>(100);
  const [correctChars, setCorrectChars] = useState<number>(0);
  const [totalChars, setTotalChars] = useState<number>(0);
  const [isComposing, setIsComposing] = useState<boolean>(false);
  const [showCompositionHint, setShowCompositionHint] = useState<boolean>(false);
  
  const textAreaRef = useRef<HTMLTextAreaElement>(null);
  const timerRef = useRef<NodeJS.Timeout | null>(null);

  // Korean character detection functions
  const isKoreanCharacter = (char: string): boolean => {
    const code = char.charCodeAt(0);
    return (code >= 0xAC00 && code <= 0xD7AF) || // Hangul syllables
           (code >= 0x3130 && code <= 0x318F);   // Hangul compatibility Jamo
  };

  const isKoreanJamo = (char: string): boolean => {
    const code = char.charCodeAt(0);
    return (code >= 0x1100 && code <= 0x11FF) || // Hangul Jamo
           (code >= 0x3130 && code <= 0x318F) || // Hangul compatibility Jamo
           (code >= 0xA960 && code <= 0xA97F);   // Hangul Jamo Extended-A
  };

  const isCompleteHangulSyllable = (char: string): boolean => {
    const code = char.charCodeAt(0);
    return code >= 0xAC00 && code <= 0xD7AF; // Complete Hangul syllables only
  };

  // Get test duration based on mode
  const getTestDuration = (mode: TestMode): number => {
    switch (mode) {
      case TestMode.FIFTEEN_SECONDS: return 15;
      case TestMode.THIRTY_SECONDS: return 30;
      case TestMode.SIXTY_SECONDS: return 60;
      case TestMode.ONE_TWENTY_SECONDS: return 120;
      default: return 30;
    }
  };

  // Load words based on language
  useEffect(() => {
    const loadWords = async () => {
      try {
        const wordCount = language === Language.KOREAN ? 30 : 50;
        const response = await typingAPI.getWords(language, wordCount);
        setDisplayText(response.words.join(' '));
        setTimeLeft(getTestDuration(mode));
      } catch (error) {
        console.error('Failed to load words:', error);
        // Fallback words
        const fallbackWords = language === Language.KOREAN 
          ? ['안녕', '세상', '컴퓨터', '키보드', '타이핑', '연습', '한글', '입력', '테스트', '속도']
          : ['the', 'quick', 'brown', 'fox', 'jumps', 'over', 'lazy', 'dog', 'and', 'to'];
        setDisplayText(fallbackWords.join(' '));
        setTimeLeft(getTestDuration(mode));
      }
    };

    loadWords();
  }, [mode, language]);

  // Reset test state
  const resetTest = useCallback(() => {
    setCurrentIndex(0);
    setUserInput('');
    setIsTestActive(false);
    setIsTestComplete(false);
    setStartTime(null);
    setWpm(0);
    setAccuracy(100);
    setCorrectChars(0);
    setTotalChars(0);
    setIsComposing(false);
    setShowCompositionHint(false);
    setTimeLeft(getTestDuration(mode));
    
    if (timerRef.current) {
      clearInterval(timerRef.current);
      timerRef.current = null;
    }
  }, [mode]);

  // Start test
  const startTest = useCallback(() => {
    if (!isTestActive && !isTestComplete) {
      setIsTestActive(true);
      setStartTime(new Date());
      
      // Start timer
      timerRef.current = setInterval(() => {
        setTimeLeft(prev => {
          if (prev <= 1) {
            setIsTestActive(false);
            setIsTestComplete(true);
            return 0;
          }
          return prev - 1;
        });
      }, 1000);
    }
  }, [isTestActive, isTestComplete]);

  // Calculate real-time stats
  useEffect(() => {
    if (isTestActive && startTime) {
      const timeElapsed = (Date.now() - startTime.getTime()) / 1000;
      if (timeElapsed > 0) {
        const wordsTyped = userInput.length / 5; // Standard: 5 characters = 1 word
        const currentWpm = Math.round((wordsTyped / timeElapsed) * 60);
        setWpm(currentWpm);

        if (totalChars > 0) {
          const currentAccuracy = Math.round((correctChars / totalChars) * 100);
          setAccuracy(currentAccuracy);
        }
      }
    }
  }, [userInput, correctChars, totalChars, isTestActive, startTime]);

  // Complete test
  useEffect(() => {
    if (isTestComplete && startTime) {
      const timeElapsed = (Date.now() - startTime.getTime()) / 1000;
      const finalStats: TypingStats = {
        wpm: wpm,
        accuracy: accuracy,
        charactersTyped: userInput.length,
        errorsCount: totalChars - correctChars,
        correctCharacters: correctChars,
        timeElapsed: timeElapsed
      };
      
      onTestComplete(finalStats);
      
      if (timerRef.current) {
        clearInterval(timerRef.current);
        timerRef.current = null;
      }
    }
  }, [isTestComplete, wpm, accuracy, userInput.length, correctChars, totalChars, startTime, onTestComplete]);

  // Generate character array for display
  const getCharacterArray = (): TypingCharacter[] => {
    const characters: TypingCharacter[] = [];
    
    for (let i = 0; i < displayText.length; i++) {
      let status: 'correct' | 'incorrect' | 'current' | 'untyped' = 'untyped';
      
      if (i < userInput.length) {
        status = userInput[i] === displayText[i] ? 'correct' : 'incorrect';
      } else if (i === currentIndex && !isComposing) {
        status = 'current';
      }
      
      characters.push({
        char: displayText[i],
        status,
        index: i
      });
    }
    
    return characters;
  };

  // Handle composition events for Korean IME
  const handleCompositionStart = useCallback(() => {
    setIsComposing(true);
    if (language === Language.KOREAN) {
      setShowCompositionHint(true);
    }
  }, [language]);

  const handleCompositionUpdate = useCallback((e: React.CompositionEvent) => {
    // During composition, show hint for Korean
    if (language === Language.KOREAN && e.data) {
      const hasJamo = Array.from(e.data).some(char => isKoreanJamo(char));
      setShowCompositionHint(hasJamo);
    }
  }, [language]);

  const handleCompositionEnd = useCallback(() => {
    setIsComposing(false);
    setShowCompositionHint(false);
  }, []);

  // Handle text input
  const handleInput = useCallback((e: React.FormEvent<HTMLTextAreaElement>) => {
    if (isTestComplete) return;

    const value = e.currentTarget.value;
    
    // For Korean language, filter out incomplete Jamo during composition
    if (language === Language.KOREAN && isComposing) {
      // During composition, don't process the input yet
      return;
    }

    // Start test on first input
    if (!isTestActive && value.length > 0) {
      startTest();
    }

    // Process the input
    let processedValue = value;
    
    // For Korean, ensure we only process complete syllables
    if (language === Language.KOREAN) {
      const filteredChars = Array.from(value).filter(char => {
        if (isKoreanCharacter(char)) {
          return isCompleteHangulSyllable(char);
        }
        return true; // Allow non-Korean characters
      });
      processedValue = filteredChars.join('');
    }

    setUserInput(processedValue);
    setCurrentIndex(processedValue.length);

    // Calculate stats
    let correct = 0;
    for (let i = 0; i < processedValue.length && i < displayText.length; i++) {
      if (processedValue[i] === displayText[i]) {
        correct++;
      }
    }
    setCorrectChars(correct);
    setTotalChars(processedValue.length);

    // Auto-complete if user typed all text correctly
    if (processedValue === displayText) {
      setIsTestActive(false);
      setIsTestComplete(true);
    }
  }, [isTestComplete, isTestActive, startTest, language, isComposing, displayText]);

  // Handle key down events
  const handleKeyDown = useCallback((e: React.KeyboardEvent<HTMLTextAreaElement>) => {
    if (isTestComplete) return;

    // For Korean IME, ignore certain keys during composition
    if (language === Language.KOREAN && isComposing) {
      // Allow backspace and delete during composition
      if (e.key !== 'Backspace' && e.key !== 'Delete') {
        return;
      }
    }

    // Prevent some default behaviors
    if (e.key === 'Tab' || e.key === 'Enter') {
      e.preventDefault();
    }
  }, [isTestComplete, language, isComposing]);

  // Handle beforeinput event to block unwanted characters
  const handleBeforeInput = useCallback((e: React.FormEvent<HTMLTextAreaElement>) => {
    if (isTestComplete) return;

    const inputEvent = e.nativeEvent as InputEvent;
    
    // For Korean language, block individual Jamo characters
    if (language === Language.KOREAN && inputEvent.data) {
      const containsIncompleteJamo = Array.from(inputEvent.data).some(char => 
        isKoreanJamo(char) && !isCompleteHangulSyllable(char)
      );
      
      if (containsIncompleteJamo && !isComposing) {
        e.preventDefault();
        return;
      }
    }
  }, [isTestComplete, language, isComposing]);

  // Focus text area on mount
  useEffect(() => {
    if (textAreaRef.current) {
      textAreaRef.current.focus();
    }
  }, []);

  // Cleanup on unmount
  useEffect(() => {
    return () => {
      if (timerRef.current) {
        clearInterval(timerRef.current);
      }
    };
  }, []);

  const characters = getCharacterArray();

  return (
    <div className="max-w-4xl mx-auto">
      {/* Stats Display */}
      <div className="flex justify-center space-x-8 mb-6">
        <div className="text-center">
          <div className="text-2xl font-bold text-primary-600">{Math.max(0, timeLeft)}</div>
          <div className="text-sm text-gray-600 dark:text-gray-400">Time</div>
        </div>
        <div className="text-center">
          <div className="text-2xl font-bold text-primary-600">{wpm}</div>
          <div className="text-sm text-gray-600 dark:text-gray-400">WPM</div>
        </div>
        <div className="text-center">
          <div className="text-2xl font-bold text-primary-600">{accuracy}%</div>
          <div className="text-sm text-gray-600 dark:text-gray-400">Accuracy</div>
        </div>
      </div>

      {/* Korean Composition Hint */}
      {language === Language.KOREAN && showCompositionHint && (
        <div className="mb-4 p-3 bg-blue-50 dark:bg-blue-900/30 border border-blue-200 dark:border-blue-800 rounded-lg text-center">
          <p className="text-sm text-blue-700 dark:text-blue-300">
            완성된 한글 글자만 입력됩니다. 자음이나 모음만으로는 처리되지 않습니다.
          </p>
        </div>
      )}

      {/* Text Display */}
      <div className="card p-6 mb-6 relative">
        <div className="text-lg leading-relaxed font-mono" style={{ lineHeight: '1.8' }}>
          {characters.map((char, index) => (
            <span
              key={index}
              className={`${
                char.status === 'correct'
                  ? 'text-green-600 bg-green-100 dark:bg-green-900/30'
                  : char.status === 'incorrect'
                  ? 'text-red-600 bg-red-100 dark:bg-red-900/30'
                  : char.status === 'current'
                  ? 'bg-blue-200 dark:bg-blue-800 border-l-2 border-blue-500'
                  : 'text-gray-500 dark:text-gray-400'
              } ${char.char === ' ' ? 'mr-1' : ''}`}
              style={{
                padding: char.char === ' ' ? '0 0.25rem' : '0',
              }}
            >
              {char.char === ' ' ? '·' : char.char}
            </span>
          ))}
        </div>

        {/* Invisible textarea for input capture */}
        <textarea
          ref={textAreaRef}
          value={userInput}
          onChange={handleInput}
          onKeyDown={handleKeyDown}
          onBeforeInput={handleBeforeInput}
          onCompositionStart={handleCompositionStart}
          onCompositionUpdate={handleCompositionUpdate}
          onCompositionEnd={handleCompositionEnd}
          className="absolute inset-0 w-full h-full opacity-0 resize-none border-none outline-none"
          disabled={isTestComplete}
          spellCheck={false}
          autoComplete="off"
          autoCorrect="off"
          autoCapitalize="off"
          style={{ 
            background: 'transparent',
            caretColor: 'transparent'
          }}
        />

        {/* Focus hint */}
        {!isTestActive && !isTestComplete && (
          <div className="absolute inset-0 flex items-center justify-center bg-gray-50 dark:bg-gray-800 bg-opacity-90 rounded">
            <div className="text-center">
              <p className="text-lg text-gray-600 dark:text-gray-400 mb-2">
                Click here to focus and start typing
              </p>
              {language === Language.KOREAN && (
                <p className="text-sm text-gray-500 dark:text-gray-500">
                  한글 입력 시 완성된 글자만 처리됩니다
                </p>
              )}
            </div>
          </div>
        )}
      </div>

      {/* Test Complete */}
      {isTestComplete && (
        <div className="card p-6 text-center">
          <h3 className="text-xl font-bold text-gray-900 dark:text-gray-100 mb-4">
            Test Complete!
          </h3>
          <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-4">
            <div>
              <div className="text-2xl font-bold text-primary-600">{wpm}</div>
              <div className="text-sm text-gray-600 dark:text-gray-400">WPM</div>
            </div>
            <div>
              <div className="text-2xl font-bold text-green-600">{accuracy}%</div>
              <div className="text-sm text-gray-600 dark:text-gray-400">Accuracy</div>
            </div>
            <div>
              <div className="text-2xl font-bold text-blue-600">{userInput.length}</div>
              <div className="text-sm text-gray-600 dark:text-gray-400">Characters</div>
            </div>
            <div>
              <div className="text-2xl font-bold text-red-600">{totalChars - correctChars}</div>
              <div className="text-sm text-gray-600 dark:text-gray-400">Errors</div>
            </div>
          </div>
          <button
            onClick={resetTest}
            className="btn-primary"
          >
            Try Again
          </button>
        </div>
      )}
    </div>
  );
};

export default TypingTest;
