import React, { useState, useEffect, useRef } from 'react';
import { TestMode, Language } from '../types';

const Home: React.FC = () => {
  const [selectedMode, setSelectedMode] = useState<TestMode>(TestMode.THIRTY_SECONDS);
  const [selectedLanguage, setSelectedLanguage] = useState<Language>(Language.ENGLISH);
  const [isTestActive, setIsTestActive] = useState(false);
  const [timeLeft, setTimeLeft] = useState(30);
  const [userInput, setUserInput] = useState('');
  const [currentIndex, setCurrentIndex] = useState(0);
  const [wpm, setWpm] = useState(0);
  const [accuracy, setAccuracy] = useState(100);
  const [isTestComplete, setIsTestComplete] = useState(false);
  const [correctChars, setCorrectChars] = useState(0);
  const [totalChars, setTotalChars] = useState(0);
  const [startTime, setStartTime] = useState<Date | null>(null);
  
  const [windowWidth, setWindowWidth] = useState(window.innerWidth);
  
  const textAreaRef = useRef<HTMLTextAreaElement>(null);
  const timerRef = useRef<NodeJS.Timeout | null>(null);

  // Listen for window resize
  useEffect(() => {
    const handleResize = () => {
      setWindowWidth(window.innerWidth);
    };
    
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  // Sample texts for different languages - Long, realistic paragraphs
  const sampleTexts = {
    english: "The art of communication has evolved dramatically throughout human history, transforming from simple gestures and sounds into sophisticated systems of written and spoken language. In today's digital age, the ability to type quickly and accurately has become an essential skill for personal and professional success. Whether you're writing emails, creating documents, programming software, or engaging in online conversations, your typing speed and accuracy directly impact your productivity and effectiveness. The development of muscle memory through consistent practice allows your fingers to move automatically across the keyboard, reducing the cognitive load required for typing and freeing your mind to focus on the content and ideas you want to express. Modern typing techniques emphasize proper posture, correct finger placement, and smooth, rhythmic keystrokes that minimize fatigue and maximize efficiency. Professional typists often achieve speeds of over one hundred words per minute while maintaining near-perfect accuracy, demonstrating the remarkable potential of human motor skills when properly trained and refined. The journey to becoming a skilled typist requires patience, dedication, and regular practice, but the rewards are substantial and long-lasting. Many successful writers, journalists, programmers, and business professionals credit their typing skills as a fundamental contributor to their career achievements. In an increasingly competitive job market, the ability to communicate quickly and effectively through written text has become a valuable asset that can set you apart from other candidates. Furthermore, the rise of remote work and digital collaboration has made typing skills even more crucial for professional success. Video conferences, instant messaging, collaborative documents, and project management tools all require efficient text input to maximize their effectiveness. The time invested in developing strong typing skills pays dividends throughout your entire career, making it one of the most practical and valuable abilities you can cultivate. Regular practice not only improves your speed and accuracy but also reduces the physical strain associated with prolonged keyboard use, helping to prevent repetitive stress injuries and maintain long-term hand and wrist health.",
    french: "L'évolution de la technologie moderne a transformé notre façon de communiquer, de travailler et d'interagir avec le monde qui nous entoure. Dans cette ère numérique, la maîtrise du clavier est devenue une compétence fondamentale qui influence directement notre productivité et notre efficacité professionnelle. La France, avec sa riche tradition littéraire et culturelle, a toujours valorisé l'expression écrite comme un art noble et raffiné. Aujourd'hui, cette tradition se prolonge dans le domaine numérique, où la capacité à taper rapidement et précisément en français constitue un atout considérable pour les étudiants, les professionnels et les créateurs de contenu. Les particularités de la langue française, avec ses accents, ses cédilles et ses caractères spéciaux, exigent une attention particulière lors de l'apprentissage de la dactylographie. La disposition AZERTY, spécifique aux pays francophones, diffère significativement du clavier QWERTY anglo-saxon, nécessitant une adaptation particulière pour ceux qui passent d'un système à l'autre. Les techniques modernes d'apprentissage de la frappe intègrent des méthodes pédagogiques innovantes qui respectent les spécificités linguistiques du français tout en développant la mémoire musculaire nécessaire à une saisie fluide et naturelle. La pratique régulière de la dactylographie française permet non seulement d'améliorer la vitesse de frappe, mais aussi de renforcer la maîtrise orthographique et grammaticale de la langue. Cette double compétence s'avère particulièrement précieuse dans le contexte professionnel contemporain, où la communication écrite occupe une place centrale dans la plupart des métiers. Les secteurs du journalisme, de l'édition, de la traduction, du secrétariat et de la communication digitale requièrent tous une excellente maîtrise de la frappe française. L'apprentissage progressif et méthodique de cette compétence contribue également au développement de la concentration et de la discipline personnelle, qualités essentielles dans notre société de l'information où les distractions numériques sont omniprésentes. La capacité à produire rapidement du texte de qualité en français ouvre de nombreuses opportunités professionnelles et créatives, permettant aux individus de s'exprimer avec aisance et précision dans tous les contextes digitaux."
  };

  const displayText = sampleTexts[selectedLanguage];

  // Get test duration
  const getTestDuration = (mode: TestMode): number => {
    switch (mode) {
      case TestMode.FIFTEEN_SECONDS: return 15;
      case TestMode.THIRTY_SECONDS: return 30;
      case TestMode.SIXTY_SECONDS: return 60;
      case TestMode.ONE_TWENTY_SECONDS: return 120;
      default: return 30;
    }
  };

  // Start test
  const startTest = () => {
    if (!isTestActive && !isTestComplete) {
      setIsTestActive(true);
      setStartTime(new Date());
      setTimeLeft(getTestDuration(selectedMode));
      
      // Start timer
      timerRef.current = setInterval(() => {
        setTimeLeft(prev => {
          if (prev <= 1) {
            setIsTestActive(false);
            setIsTestComplete(true);
            return 0;
          }
          return prev - 1;
        });
      }, 1000);
    }
  };

  // Reset test
  const resetTest = () => {
    setIsTestActive(false);
    setIsTestComplete(false);
    setUserInput('');
    setCurrentIndex(0);
    setWpm(0);
    setAccuracy(100);
    setCorrectChars(0);
    setTotalChars(0);
    setStartTime(null);
    setTimeLeft(getTestDuration(selectedMode));
    
    if (timerRef.current) {
      clearInterval(timerRef.current);
      timerRef.current = null;
    }
  };

  // Handle input
  const handleInput = (e: React.FormEvent<HTMLTextAreaElement>) => {
    if (isTestComplete) return;
    
    const value = e.currentTarget.value;
    
    // Start test on first input
    if (!isTestActive && value.length > 0) {
      startTest();
    }
    
    // If user reached the end of text, complete the test
    if (value.length >= displayText.length) {
      setIsTestActive(false);
      setIsTestComplete(true);
      if (timerRef.current) {
        clearInterval(timerRef.current);
        timerRef.current = null;
      }
      return;
    }
    
    setUserInput(value);
    setCurrentIndex(value.length);
    
    // Calculate accuracy
    let correct = 0;
    for (let i = 0; i < value.length && i < displayText.length; i++) {
      if (value[i] === displayText[i]) {
        correct++;
      }
    }
    setCorrectChars(correct);
    setTotalChars(value.length);
    
    if (value.length > 0) {
      setAccuracy(Math.round((correct / value.length) * 100));
    }
  };

  // Calculate WPM
  useEffect(() => {
    if (isTestActive && startTime) {
      const timeElapsed = (Date.now() - startTime.getTime()) / 1000;
      if (timeElapsed > 0) {
        const wordsTyped = userInput.length / 5; // Standard: 5 characters = 1 word
        const currentWpm = Math.round((wordsTyped / timeElapsed) * 60);
        setWpm(currentWpm);
      }
    }
  }, [userInput, isTestActive, startTime]);

  // Reset when mode or language changes
  useEffect(() => {
    const resetTestFunction = () => {
      setIsTestActive(false);
      setIsTestComplete(false);
      setUserInput('');
      setCurrentIndex(0);
      setWpm(0);
      setAccuracy(100);
      setCorrectChars(0);
      setTotalChars(0);
      setStartTime(null);
      setTimeLeft(getTestDuration(selectedMode));
      
      if (timerRef.current) {
        clearInterval(timerRef.current);
        timerRef.current = null;
      }
    };
    
    resetTestFunction();
  }, [selectedMode, selectedLanguage]);

  // Cleanup
  useEffect(() => {
    return () => {
      if (timerRef.current) {
        clearInterval(timerRef.current);
      }
    };
  }, []);

  // Split text into words and lines for display - much more aggressive for mobile
  const getWordsAndLines = () => {
    const words = displayText.split(' ');
    const lines: string[] = [];
    let currentLine: string[] = [];
    let currentLineLength = 0;
    // Very aggressive line length - ensure 3 lines always fit
    const maxLineLength = windowWidth < 576 ? 20 : windowWidth < 768 ? 30 : windowWidth < 1024 ? 40 : windowWidth < 1440 ? 55 : 65;
    
    words.forEach(word => {
      if (currentLineLength + word.length + 1 > maxLineLength && currentLine.length > 0) {
        lines.push(currentLine.join(' '));
        currentLine = [word];
        currentLineLength = word.length;
      } else {
        currentLine.push(word);
        currentLineLength += word.length + (currentLine.length > 1 ? 1 : 0);
      }
    });
    
    if (currentLine.length > 0) {
      lines.push(currentLine.join(' '));
    }
    
    return lines;
  };

  // Get current line based on user input - fix space calculation
  const getCurrentLineInfo = () => {
    const lines = getWordsAndLines();
    let charCount = 0;
    let currentLineIndex = 0;
    
    for (let i = 0; i < lines.length; i++) {
      const lineLength = lines[i].length;
      const lineEndIndex = charCount + lineLength;
      
      if (currentIndex <= lineEndIndex) {
        currentLineIndex = i;
        break;
      }
      charCount += lineLength + 1; // +1 for space between lines
    }
    
    return {
      lines,
      currentLineIndex,
      startCharIndex: charCount
    };
  };

  // Generate character display for visible lines only - Always show exactly 3 lines
  const getCharacterDisplay = () => {
    const { lines, currentLineIndex } = getCurrentLineInfo();
    // Always show exactly 3 lines - current line and 2 more
    const visibleLines = lines.slice(currentLineIndex, currentLineIndex + 3);
    const startLineIndex = currentLineIndex;
    
    let globalCharIndex = 0;
    // Calculate starting character index for visible lines
    for (let i = 0; i < startLineIndex; i++) {
      globalCharIndex += lines[i].length + (i < lines.length - 1 ? 1 : 0);
    }
    
    // Ensure we always have exactly 3 lines by padding with empty lines if needed
    while (visibleLines.length < 3) {
      visibleLines.push('');
    }
    
    return visibleLines.map((line, lineIndex) => {
      const actualLineIndex = startLineIndex + lineIndex;
      const chars = [];
      
      if (line === '') {
        // Empty line padding
        return (
          <div key={`empty-${actualLineIndex}`} className="leading-tight break-words h-[1.5rem]">
            &nbsp;
          </div>
        );
      }
      
      for (let i = 0; i < line.length; i++) {
        const globalIndex = globalCharIndex + i;
        let className = 'text-gray-400';
        
        if (globalIndex < userInput.length) {
          if (userInput[globalIndex] === displayText[globalIndex]) {
            className = 'text-green-400';
          } else {
            className = 'text-red-400 bg-red-900/30';
          }
        } else if (globalIndex === currentIndex && isTestActive) {
          className = 'bg-yellow-400 text-gray-900';
        }
        
        chars.push(
          <span key={globalIndex} className={className}>
            {line[i]}
          </span>
        );
      }
      
      globalCharIndex += line.length + (actualLineIndex < lines.length - 1 ? 1 : 0);
      
      return (
        <div key={actualLineIndex} className="leading-tight break-words h-[1.2rem]">
          {chars}
        </div>
      );
    });
  };

  return (
    <div className="min-h-screen bg-gray-900 text-gray-300">
      <div className="container mx-auto px-4 py-8 max-w-[95%]">
        {/* Header - More compact */}
        <div className="text-center mb-12">
          <h1 className="text-3xl font-bold text-yellow-400 mb-2">
            TurboKeys
          </h1>
        </div>

        {/* Mode Selection - Centered and compact */}
        <div className="flex justify-center mb-12">
          <div className="flex flex-col items-center gap-6">
            <div className="flex flex-wrap gap-2 justify-center">
              {Object.values(TestMode).map((mode) => (
                <button
                  key={mode}
                  onClick={() => {
                    setSelectedMode(mode);
                    resetTest();
                  }}
                  className={`px-4 py-2 rounded text-sm font-medium transition-colors ${
                    selectedMode === mode
                      ? 'bg-yellow-400 text-gray-900'
                      : 'bg-gray-800 text-gray-400 hover:bg-gray-700 hover:text-gray-300'
                  }`}
                >
                  {mode}
                </button>
              ))}
            </div>

            <div className="flex flex-wrap gap-2 justify-center">
              {Object.values(Language).map((language) => (
                <button
                  key={language}
                  onClick={() => {
                    setSelectedLanguage(language);
                    resetTest();
                  }}
                  className={`px-4 py-2 rounded text-sm font-medium transition-colors ${
                    selectedLanguage === language
                      ? 'bg-yellow-400 text-gray-900'
                      : 'bg-gray-800 text-gray-400 hover:bg-gray-700 hover:text-gray-300'
                  }`}
                >
                  {language === 'french' ? 'Français' : 'English'}
                </button>
              ))}
            </div>
          </div>
        </div>

        {/* Typing Test - Full width, no visible container like Monkeytype */}
        <div className="max-w-[90%] mx-auto px-8">
          {/* Stats Display */}
          <div className="flex justify-center space-x-8 mb-6">
            <div className="text-center">
              <div className="text-3xl font-bold text-yellow-400">{timeLeft}</div>
              <div className="text-sm text-gray-400">Time</div>
            </div>
            <div className="text-center">
              <div className="text-3xl font-bold text-yellow-400">{wpm}</div>
              <div className="text-sm text-gray-400">WPM</div>
            </div>
            <div className="text-center">
              <div className="text-3xl font-bold text-yellow-400">{accuracy}%</div>
              <div className="text-sm text-gray-400">Accuracy</div>
            </div>
          </div>
          
          {/* Typing Area */}
          <div 
            className="bg-gray-800 rounded-lg p-4 mb-6 relative cursor-text w-full overflow-hidden"
            style={{ height: '200px' }}
            onClick={() => textAreaRef.current?.focus()}
          >
            <div className="text-3xl leading-tight font-mono max-w-full mx-auto space-y-2 break-words overflow-wrap-anywhere h-full flex flex-col justify-center">
              {getCharacterDisplay()}
            </div>
            
            {/* Invisible textarea for input */}
            <textarea
              ref={textAreaRef}
              value={userInput}
              onChange={handleInput}
              className="absolute inset-0 w-full h-full opacity-0 resize-none outline-none"
              disabled={isTestComplete}
              placeholder=""
            />
            
            {/* Focus overlay */}
            {!isTestActive && !isTestComplete && (
              <div className="absolute inset-0 bg-gray-800 bg-opacity-90 rounded-lg flex items-center justify-center">
                <div className="text-center text-gray-400">
                  <p className="text-xl mb-2">Click here to start typing</p>
                  <p className="text-sm">Selected: {selectedMode} • {selectedLanguage === 'french' ? 'Français' : 'English'}</p>
                </div>
              </div>
            )}
          </div>

          {/* Test Complete Screen */}
          {isTestComplete && (
            <div className="text-center bg-gray-800 rounded-lg p-8 mb-6">
              <h2 className="text-2xl font-bold text-yellow-400 mb-4">Test Complete!</h2>
              <div className="grid grid-cols-3 gap-8 mb-6">
                <div className="text-center">
                  <div className="text-3xl font-bold text-yellow-400">{wpm}</div>
                  <div className="text-sm text-gray-400">WPM</div>
                </div>
                <div className="text-center">
                  <div className="text-3xl font-bold text-green-400">{accuracy}%</div>
                  <div className="text-sm text-gray-400">Accuracy</div>
                </div>
                <div className="text-center">
                  <div className="text-3xl font-bold text-blue-400">{correctChars}/{totalChars}</div>
                  <div className="text-sm text-gray-400">Correct</div>
                </div>
              </div>
              <button 
                onClick={resetTest}
                className="bg-yellow-400 text-gray-900 px-6 py-2 rounded font-medium hover:bg-yellow-300 transition-colors"
              >
                Try Again
              </button>
            </div>
          )}

          {!isTestActive && !isTestComplete && (
            <div className="text-center text-gray-400 mb-4">
              <p className="text-sm">Click on the text above to start typing</p>
            </div>
          )}
        </div>

        {/* Instructions - More compact and bottom */}
        <div className="max-w-4xl mx-auto mt-16 text-center">
          <div className="text-sm text-gray-500 space-y-1">
            <p>Click on the text area to start • Type the words exactly as shown • Use backspace to correct mistakes</p>
            <p>Your WPM and accuracy will be calculated in real-time</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Home;
