import React, { createContext, useContext, useState, ReactNode, useCallback } from 'react';

export type NotificationType = 'success' | 'error' | 'warning';

export interface Notification {
  id: number;
  message: string;
  type: NotificationType;
}

interface NotificationContextProps {
  notify: (message: string, type: NotificationType) => void;
}

const NotificationContext = createContext<NotificationContextProps | undefined>(undefined);

let notificationId = 0;

export const NotificationProvider: React.FC<{ children: ReactNode }> = ({ children }) => {
  const [notifications, setNotifications] = useState<Notification[]>([]);

  const notify = useCallback((message: string, type: NotificationType) => {
    const id = ++notificationId;
    setNotifications((prev) => [...prev, { id, message, type }]);
    setTimeout(() => {
      setNotifications((prev) => prev.filter((n) => n.id !== id));
    }, 4000);
  }, []);

  return (
    <NotificationContext.Provider value={{ notify }}>
      {children}
      <div className="fixed top-4 right-4 z-[9999] flex flex-col space-y-3 items-end">
        {notifications.map((n) => (
          <div
            key={n.id}
            className={`min-w-[220px] max-w-xs px-4 py-3 rounded shadow-lg border-l-4 relative animate-slide-in-right
              ${n.type === 'error' ? 'bg-red-50 dark:bg-red-900/70 border-red-500 text-red-700 dark:text-red-300' : ''}
              ${n.type === 'success' ? 'bg-green-50 dark:bg-green-900/70 border-green-500 text-green-700 dark:text-green-300' : ''}
              ${n.type === 'warning' ? 'bg-yellow-50 dark:bg-yellow-900/70 border-yellow-500 text-yellow-700 dark:text-yellow-300' : ''}
            `}
            style={{ borderStyle: 'solid', borderWidth: '1px 0 1px 4px', borderRadius: '8px', borderRight: 'none' }}
          >
            <span className="font-semibold capitalize">{n.type}</span>
            <span className="mx-2">|</span>
            <span>{n.message}</span>
            <button
              className="absolute top-1 right-2 text-lg text-gray-400 hover:text-gray-700 dark:hover:text-white"
              onClick={() => setNotifications((prev) => prev.filter((x) => x.id !== n.id))}
              aria-label="Close notification"
            >
              &times;
            </button>
          </div>
        ))}
      </div>
    </NotificationContext.Provider>
  );
};

export function useNotification() {
  const ctx = useContext(NotificationContext);
  if (!ctx) throw new Error('useNotification must be used within a NotificationProvider');
  return ctx;
}

