import React, { useState, useEffect, useCallback, useRef } from 'react';
import { TestMode, Language, TypingStats, TypingCharacter } from '../../types';
import { typingAPI } from '../../services/api';
import { useAuth } from '../../contexts/AuthContext';

interface TypingTestProps {
  mode: TestMode;
  language: Language;
  onTestComplete: (stats: TypingStats) => void;
}

const TypingTest: React.FC<TypingTestProps> = ({ mode, language, onTestComplete }) => {
  const { user } = useAuth();
  const [words, setWords] = useState<string[]>([]);
  const [currentText, setCurrentText] = useState<string>('');
  const [typedText, setTypedText] = useState<string>('');
  const [currentCharIndex, setCurrentCharIndex] = useState<number>(0);
  const [characters, setCharacters] = useState<TypingCharacter[]>([]);
  const [startTime, setStartTime] = useState<number | null>(null);
  const [timeLeft, setTimeLeft] = useState<number>(parseInt(mode.replace('s', '')));
  const [isActive, setIsActive] = useState<boolean>(false);
  const [isCompleted, setIsCompleted] = useState<boolean>(false);
  const [isFocused, setIsFocused] = useState<boolean>(true);
  const [stats, setStats] = useState<TypingStats>({
    wpm: 0,
    accuracy: 0,
    charactersTyped: 0,
    errorsCount: 0,
    correctCharacters: 0,
    timeElapsed: 0,
  });

  const containerRef = useRef<HTMLDivElement>(null);

  // Load words when component mounts
  useEffect(() => {
    const loadWords = async () => {
      try {
        const response = await typingAPI.getWords(language, 200);
        setWords(response.words);
        generateText(response.words);
      } catch (error) {
        console.error('Failed to load words:', error);
        // Fallback words
        const fallbackWords = [
          'the', 'quick', 'brown', 'fox', 'jumps', 'over', 'lazy', 'dog',
          'pack', 'my', 'box', 'with', 'five', 'dozen', 'liquor', 'jugs',
          'time', 'part', 'then', 'and', 'what', 'course', 'stand', 'to',
          'home', 'around', 'over', 'might', 'move', 'to', 'just', 'these',
          'must', 'look', 'move', 'could', 'go', 'begin', 'people', 'what',
          'tell', 'year', 'high', 'there', 'eye', 'world', 'way', 'only',
          'work', 'word', 'can', 'point', 'down', 'form', 'thing', 'govern',
          'life', 'any', 'few', 'place', 'set'
        ];
        setWords(fallbackWords);
        generateText(fallbackWords);
      }
    };

    loadWords();
  }, [language, generateText]);

  // Timer effect
  useEffect(() => {
    let interval: NodeJS.Timeout;

    if (isActive && timeLeft > 0 && !isCompleted) {
      interval = setInterval(() => {
        setTimeLeft((time) => {
          if (time <= 1) {
            completeTest();
            return 0;
          }
          return time - 1;
        });
      }, 1000);
    }

    return () => clearInterval(interval);
  }, [isActive, timeLeft, isCompleted, completeTest]);

  // Focus management
  useEffect(() => {
    const handleFocus = () => setIsFocused(true);
    const handleBlur = () => setIsFocused(false);
    
    const container = containerRef.current;
    if (container) {
      container.addEventListener('focus', handleFocus);
      container.addEventListener('blur', handleBlur);
      container.focus();
    }

    return () => {
      if (container) {
        container.removeEventListener('focus', handleFocus);
        container.removeEventListener('blur', handleBlur);
      }
    };
  }, []);

  // Keyboard event handler
  useEffect(() => {
    const handleKeyDown = (event: KeyboardEvent) => {
      if (isCompleted || !isFocused) return;

      // Prevent default behavior for most keys
      if (event.key !== 'F5' && event.key !== 'F12' && !event.ctrlKey) {
        event.preventDefault();
      }

      // Start test on first keystroke
      if (!isActive && !isCompleted && event.key.length === 1) {
        startTest();
      }

      if (event.key === 'Backspace') {
        handleBackspace();
      } else if (event.key.length === 1) {
        handleCharacterInput(event.key);
      }
    };

    document.addEventListener('keydown', handleKeyDown);
    return () => document.removeEventListener('keydown', handleKeyDown);
  }, [isActive, isCompleted, isFocused, startTest, handleBackspace, handleCharacterInput]);

  // Generate random text from words
  const generateText = useCallback((wordList: string[]) => {
    const shuffled = [...wordList].sort(() => 0.5 - Math.random());
    const text = shuffled.slice(0, 50).join(' ');
    setCurrentText(text);
    setCharacters(text.split('').map((char, index) => ({
      char,
      status: index === 0 ? 'current' : 'untyped',
      index,
    })));
  }, []);

  const startTest = useCallback(() => {
    if (!isActive && !isCompleted) {
      setIsActive(true);
      setStartTime(Date.now());
    }
  }, [isActive, isCompleted]);

  const handleCharacterInput = useCallback((char: string) => {
    if (isCompleted || currentCharIndex >= currentText.length) return;

    const newTypedText = typedText + char;
    setTypedText(newTypedText);

    const newCharacters = [...characters];
    const currentChar = currentText[currentCharIndex];
    
    // Mark current character as correct or incorrect
    newCharacters[currentCharIndex].status = char === currentChar ? 'correct' : 'incorrect';
    
    // Move to next character
    const nextIndex = currentCharIndex + 1;
    setCurrentCharIndex(nextIndex);
    
    // Set next character as current
    if (nextIndex < currentText.length) {
      newCharacters[nextIndex].status = 'current';
    }

    setCharacters(newCharacters);

    // Complete test if all text is typed
    if (nextIndex >= currentText.length) {
      completeTest();
    }
  }, [isCompleted, currentCharIndex, currentText.length, typedText, characters, currentText]);

  const handleBackspace = useCallback(() => {
    if (currentCharIndex === 0) return;

    const newTypedText = typedText.slice(0, -1);
    setTypedText(newTypedText);

    const newCharacters = [...characters];
    
    // Reset current character to untyped
    newCharacters[currentCharIndex].status = 'untyped';
    
    // Move back one character
    const prevIndex = currentCharIndex - 1;
    setCurrentCharIndex(prevIndex);
    
    // Set previous character as current
    newCharacters[prevIndex].status = 'current';

    setCharacters(newCharacters);
  }, [currentCharIndex, typedText, characters]);

  const calculateStats = useCallback((): TypingStats => {
    const timeElapsed = startTime ? (Date.now() - startTime) / 1000 : 0;
    const correctCharacters = characters.filter(char => char.status === 'correct').length;
    const incorrectCharacters = characters.filter(char => char.status === 'incorrect').length;
    const totalTyped = correctCharacters + incorrectCharacters;
    
    const wpm = timeElapsed > 0 ? Math.round((correctCharacters / 5) / (timeElapsed / 60)) : 0;
    const accuracy = totalTyped > 0 ? Math.round((correctCharacters / totalTyped) * 100) : 100;

    return {
      wpm,
      accuracy,
      charactersTyped: totalTyped,
      errorsCount: incorrectCharacters,
      correctCharacters,
      timeElapsed: Math.round(timeElapsed),
    };
  }, [characters, startTime]);

  const completeTest = useCallback(() => {
    setIsActive(false);
    setIsCompleted(true);
    const finalStats = calculateStats();
    setStats(finalStats);
    onTestComplete(finalStats);
  }, [calculateStats, onTestComplete]);

  const resetTest = () => {
    setTypedText('');
    setCurrentCharIndex(0);
    setStartTime(null);
    setTimeLeft(parseInt(mode.replace('s', '')));
    setIsActive(false);
    setIsCompleted(false);
    setStats({
      wpm: 0,
      accuracy: 0,
      charactersTyped: 0,
      errorsCount: 0,
      correctCharacters: 0,
      timeElapsed: 0,
    });
    generateText(words);
    
    if (containerRef.current) {
      containerRef.current.focus();
    }
  };

  // Update stats in real-time
  useEffect(() => {
    if (isActive && !isCompleted) {
      const currentStats = calculateStats();
      setStats(currentStats);
    }
  }, [characters, isActive, isCompleted, calculateStats]);

  const getCharacterClassName = (char: TypingCharacter) => {
    switch (char.status) {
      case 'correct':
        return 'text-green-400';
      case 'incorrect':
        return 'text-red-400 bg-red-100 dark:bg-red-900';
      case 'current':
        return 'bg-blue-500 text-white animate-pulse';
      default:
        return 'text-gray-600 dark:text-gray-400';
    }
  };

  return (
    <div className="w-full max-w-4xl mx-auto p-6">
      {/* Stats Display */}
      <div className="flex justify-between items-center mb-6 p-4 card">
        <div className="flex space-x-6">
          <div className="text-center">
            <div className="text-2xl font-bold text-primary-600">{stats.wpm}</div>
            <div className="text-sm text-gray-600 dark:text-gray-400">WPM</div>
          </div>
          <div className="text-center">
            <div className="text-2xl font-bold text-green-600">{stats.accuracy}%</div>
            <div className="text-sm text-gray-600 dark:text-gray-400">Accuracy</div>
          </div>
          <div className="text-center">
            <div className="text-2xl font-bold text-blue-600">{stats.errorsCount}</div>
            <div className="text-sm text-gray-600 dark:text-gray-400">Errors</div>
          </div>
        </div>
        <div className="text-center">
          <div className="text-3xl font-bold text-orange-600">{timeLeft}</div>
          <div className="text-sm text-gray-600 dark:text-gray-400">Seconds</div>
        </div>
      </div>

      {/* Text Display */}
      <div 
        ref={containerRef}
        className="mb-6 p-6 card typing-text min-h-[200px] leading-loose cursor-text focus:outline-none focus:ring-2 focus:ring-primary-500 focus:ring-opacity-50 relative"
        tabIndex={0}
        onClick={() => containerRef.current?.focus()}
      >
        {/* Focus indicator */}
        {!isFocused && !isCompleted && (
          <div className="absolute inset-0 flex items-center justify-center bg-gray-900 bg-opacity-50 rounded-lg">
            <div className="text-white text-center">
              <div className="text-xl font-bold mb-2">Click here to focus</div>
              <div className="text-sm">Start typing to begin the test</div>
            </div>
          </div>
        )}
        
        <div className="text-xl font-mono select-none">
          {characters.map((char, index) => (
            <span
              key={index}
              className={`${getCharacterClassName(char)} ${char.char === ' ' ? 'mr-1' : ''} transition-colors duration-100`}
            >
              {char.char === ' ' ? '\u00A0' : char.char}
            </span>
          ))}
        </div>
      </div>

      {/* Status Messages */}
      {!isActive && !isCompleted && isFocused && (
        <div className="text-center mb-6 p-4 bg-blue-50 dark:bg-blue-900 rounded-lg">
          <p className="text-blue-700 dark:text-blue-300">
            Start typing to begin the test. Time will start counting when you type the first character.
          </p>
        </div>
      )}

      {/* Controls */}
      <div className="flex justify-center space-x-4">
        <button
          onClick={resetTest}
          className="btn-secondary"
        >
          Reset Test
        </button>
        {isCompleted && user && (
          <button
            onClick={() => {
              // Save test result to backend
              typingAPI.submitTest({
                wpm: stats.wpm,
                accuracy: stats.accuracy,
                charactersTyped: stats.charactersTyped,
                errorsCount: stats.errorsCount,
                duration: parseInt(mode.replace('s', '')),
                mode,
                language,
                text: currentText,
              });
            }}
            className="btn-primary"
          >
            Save Result
          </button>
        )}
      </div>

      {/* Completion Message */}
      {isCompleted && (
        <div className="mt-6 p-4 card text-center">
          <h3 className="text-xl font-bold mb-2">Test Completed!</h3>
          <p className="text-gray-600 dark:text-gray-400">
            You typed at {stats.wpm} WPM with {stats.accuracy}% accuracy
          </p>
          <div className="mt-4 grid grid-cols-2 md:grid-cols-4 gap-4 text-sm">
            <div className="p-2 bg-gray-50 dark:bg-gray-700 rounded">
              <div className="font-semibold">Characters</div>
              <div>{stats.charactersTyped}</div>
            </div>
            <div className="p-2 bg-gray-50 dark:bg-gray-700 rounded">
              <div className="font-semibold">Correct</div>
              <div className="text-green-600">{stats.correctCharacters}</div>
            </div>
            <div className="p-2 bg-gray-50 dark:bg-gray-700 rounded">
              <div className="font-semibold">Errors</div>
              <div className="text-red-600">{stats.errorsCount}</div>
            </div>
            <div className="p-2 bg-gray-50 dark:bg-gray-700 rounded">
              <div className="font-semibold">Time</div>
              <div>{stats.timeElapsed}s</div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default TypingTest;
