import React, { useState } from 'react';
import { TestMode, Language, TypingStats } from '../types';

const Home: React.FC = () => {
  const [selectedMode, setSelectedMode] = useState<TestMode>(TestMode.THIRTY_SECONDS);
  const [selectedLanguage, setSelectedLanguage] = useState<Language>(Language.ENGLISH);
  const [testKey, setTestKey] = useState<number>(0);

  const handleTestComplete = (stats: TypingStats) => {
    console.log('Test completed with stats:', stats);
  };

  const resetTest = () => {
    setTestKey(prev => prev + 1);
  };

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900 py-8">
      <div className="container mx-auto px-4">
        {/* Header */}
        <div className="text-center mb-8">
          <h1 className="text-4xl font-bold text-gray-900 dark:text-gray-100 mb-2">
            TurboKeys
          </h1>
          <p className="text-gray-600 dark:text-gray-400">
            Test your typing speed and accuracy
          </p>
        </div>

        {/* Mode Selection */}
        <div className="flex justify-center mb-8">
          <div className="card p-4">
            <div className="flex flex-wrap gap-2 mb-4">
              <span className="text-sm font-medium text-gray-700 dark:text-gray-300 mr-2">
                Mode:
              </span>
              {Object.values(TestMode).map((mode) => (
                <button
                  key={mode}
                  onClick={() => {
                    setSelectedMode(mode);
                    resetTest();
                  }}
                  className={`px-3 py-1 rounded text-sm font-medium transition-colors ${
                    selectedMode === mode
                      ? 'bg-primary-600 text-white'
                      : 'bg-gray-200 dark:bg-gray-700 text-gray-700 dark:text-gray-300 hover:bg-gray-300 dark:hover:bg-gray-600'
                  }`}
                >
                  {mode}
                </button>
              ))}
            </div>

            <div className="flex flex-wrap gap-2">
              <span className="text-sm font-medium text-gray-700 dark:text-gray-300 mr-2">
                Language:
              </span>
              {Object.values(Language).map((language) => (
                <button
                  key={language}
                  onClick={() => {
                    setSelectedLanguage(language);
                    resetTest();
                  }}
                  className={`px-3 py-1 rounded text-sm font-medium transition-colors capitalize ${
                    selectedLanguage === language
                      ? 'bg-primary-600 text-white'
                      : 'bg-gray-200 dark:bg-gray-700 text-gray-700 dark:text-gray-300 hover:bg-gray-300 dark:hover:bg-gray-600'
                  }`}
                >
                  {language === 'korean' ? '한국어' : language}
                </button>
              ))}
            </div>
          </div>
        </div>

        {/* Typing Test */}
        <TypingTest
          key={testKey}
          mode={selectedMode}
          language={selectedLanguage}
          onTestComplete={handleTestComplete}
        />

        {/* Instructions */}
        <div className="max-w-2xl mx-auto mt-8 card p-6">
          <h2 className="text-xl font-bold mb-4 text-gray-900 dark:text-gray-100">
            How to use
          </h2>
          <div className="space-y-2 text-gray-600 dark:text-gray-400">
            <p>• Click on the text area to focus and start typing</p>
            <p>• Type the words exactly as shown - no input field needed!</p>
            <p>• Use backspace to correct mistakes</p>
            <p>• Your WPM and accuracy will be calculated in real-time</p>
            <p>• Create an account to save your results and compete on leaderboards</p>
            <p>• Press any key to start the timer automatically</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Home;
